const {useState, useEffect} = React;


function App(){

    const [count, setCount] = useState(0);

    useEffect(() => {

        console.log('Count ha cambiado: ', count);

    }, [count]);


    const increment = () => {

        setCount(value => value + 1);
    }

    const decrement = () => {

        setCount(value => value - 1);
    }

    return (<div className="App">
    
        <h1 className="p-2 text-center">React Template</h1>

        <h2 className="p-2 text-center">{count}</h2>

        <div className="m-auto d-flex justify-content-evenly" style={{maxWidth: '400px'}}>
        
            <button className="btn btn-success btn-sm px-4" onClick={increment}>+</button>
            
            <button className="btn btn-danger btn-sm px-4" onClick={decrement}>-</button>
        </div>
    </div>);
}


const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(
    <React.StrictMode>
        <App />
    </React.StrictMode>
);
